module Test.Ethereum.Block
( tests
) where

import qualified Data.ByteString as B

import Test.Tasty
import Test.Tasty.HUnit

import Text.RawString.QQ

-- internal modules

import Ethereum.Block
import Ethereum.Receipt

import Test.Utils

-- -------------------------------------------------------------------------- --
-- Test cases

tests :: TestTree
tests = testGroup "Block tests"
    [ testGroup "jsonRpcRoundtrips"
        [ testCase "getBlockByHash 0" $ jsonRpcRoundtripTest @RpcBlock getBlockByHash_0
        , testCase "getBlockByNumber 0" $ jsonRpcRoundtripTest @RpcBlock getBlockByNumber_0
        , testCase "getReceipt 0" $ jsonRpcRoundtripTest @RpcReceipt getReceipt_0
        ]
    , testGroup "rlpRoundtrips"
        $ fmap (\(l, t) -> testCase (show @Int l) (rlpRoundtripTest @Block t))
        $ zip [0..] rlpTestCases
    ]

